import React, { Component } from "react";

export default class Userdetails extends Component {
  constructor (props) {
    super(props);
    this.state = {
      userData: "",
      FinancialData:"",
    };
  }


  componentDidMount(){
		fetch("http://localhost:5000/financialdata", {
      method: "POST",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({
        token:window.localStorage.getItem("token"),
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data,"financialdata");
        this.setState({FinancialData: data.data});

        });
	}
	componentDidMount(){
		fetch("http://localhost:5000/userData", {
      method: "POST",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({
        token:window.localStorage.getItem("token"),
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data,"userData");
        this.setState({userData: data.data});

        });
	}
  logOut=() =>{
    window.localStorage.clear();
    window.location.href="./sign-in"
  }
	render() {
    return (
        <div>
          <h1>Account Details</h1>
           <br /><hr /><br /> 
            Name: <h1>{this.state.userData.fname}</h1>
            Email: <h1>{this.state.userData.email}</h1><br/>
           <hr /><br /> 
          <h1>Closed End Loan</h1>
            Loan Amount: <h1>{this.state.FinancialData.clamount}</h1>
            Loan Term: <h1>{this.state.FinancialData.clterm}</h1>
            Interest Rate: <h1>{this.state.FinancialData.cirate}</h1>
            Backend Cost: <h1>{this.state.FinancialData.cbcost}</h1>
            <br />
          <h1>Credit Card</h1>
            Balance: <h1>{this.state.FinancialData.ccbal}</h1>
            APR: <h1>{this.state.FinancialData.ccapr}</h1>
            <br />
          <h1>Deposit Account</h1> 
            Deposit Amount: <h1>{this.state.FinancialData.damt}</h1>
            APY: <h1>{this.state.FinancialData.dapy}</h1>
            Term: <h1>{this.state.FinancialData.dlterm}</h1>
            <br />
          <h1>Mortgage Loan</h1>
            Loan Amount: <h1>{this.state.FinancialData.mlamount}</h1>
            Loan Term: <h1>{this.state.FinancialData.mlterm}</h1>
            APR: <h1>{this.state.FinancialData.mirate}</h1>
            BCost: <h1>{this.state.FinancialData.mbcost}</h1>
            <br />
            <button onClick={this.logOut}>Log Out</button>
          </div>
        )
      }
}
